const reducer = (state, action) => {
  if (action.type === 'INCREASE') {
    const tempCart = state.cart.map((item) => {
      if (item.id === action.payload) {
        return { ...item, amount: item.amount + 1 };
      }
      return item;
    });
    return { ...state, cart: tempCart };
  }
  // Інші дії...
  return state;
};
